<?phpnamespace App\Modules\Exam\Models;use App\Libraries\CommonFunction;use Illuminate\Database\Eloquent\Model;use Illuminate\Support\Facades\Auth;class ExamEmailQueue extends Model {    protected $table = 'ex_email_queue';    protected $fillable = [        'id',        'app_id',        'service_id',        'email_content',        'sms_content',        'email_to',        'sms_to',        'email_cc',        'attachment',        'secret_key',        'pdf_type',        'email_status',        'sms_status',        'created_at',        'updated_at',    ];    public static function boot() {        if (Auth::user()) {            parent::boot();            static::creating(function ($post) {                $post->created_by = CommonFunction::getUserId();                $post->updated_by = CommonFunction::getUserId();            });            static::updating(function ($post) {                $post->updated_by = CommonFunction::getUserId();            });        }    }    //**********************End of Model Class***************** */}