<?phpnamespace App\Modules\Exam\Models;use App\Libraries\CommonFunction;use Illuminate\Database\Eloquent\Model;use Illuminate\Support\Facades\Auth;use Illuminate\Support\Facades\DB;class Question extends Model {    protected $table = 'ex_question';    protected $fillable = [        'id',        'question_type',        'question_name',        'additional_part',        'is_archive',        'created_by',        'updated_by'    ];    public static function boot() {        if (Auth::user()) {            parent::boot();            static::creating(function ($post) {                $post->created_by = CommonFunction::getUserId();                $post->updated_by = CommonFunction::getUserId();            });            static::updating(function ($post) {                $post->updated_by = CommonFunction::getUserId();            });        }    }    /**     * @return application list     */    public static function getQuestionList()    {        DB::statement(DB::raw('set @serial=0'));        $result = Question::leftJoin('ex_exam_type as et','et.id','=','ex_question.question_type')            ->orderBy('ex_question.id', 'desc')            ->where('ex_question.is_archive', 0)            ->get([                'ex_question.id',                'et.exam_name',                'ex_question.question_name',                'ex_question.updated_at as last_update',                DB::raw('@serial  := @serial  + 1 AS serial')            ]);        return $result;    }    //**********************End of Model Class***************** */}