<?phpnamespace App\Modules\Exam\Models;use App\Libraries\CommonFunction;use Illuminate\Database\Eloquent\Model;use Illuminate\Support\Facades\Auth;class QuestionOption extends Model {    protected $table = 'ex_question_option';    protected $fillable = [        'id',        'question_id',        'option_name',        'option_no',        'is_correct_answer',        'is_archive',        'created_by',        'updated_by'    ];    public static function boot() {        if (Auth::user()) {            parent::boot();            static::creating(function ($post) {                $post->created_by = CommonFunction::getUserId();                $post->updated_by = CommonFunction::getUserId();            });            static::updating(function ($post) {                $post->updated_by = CommonFunction::getUserId();            });        }    }    //**********************End of Model Class***************** */}