<?phpnamespace App\Modules\Exam\Models;use App\Libraries\CommonFunction;use Illuminate\Database\Eloquent\Model;use Illuminate\Support\Facades\Auth;class ScheduleQuestion extends Model {    protected $table = 'ex_schedule_question';    protected $fillable = [        'id',        'schedule_id',        'question_type',        'question_name',        'additional_part',        'question_id',        'is_archive',        'created_by',        'updated_by',    ];        public static function boot()    {        if (Auth::user()) {            parent::boot();                        static::creating(function ($post) {                $post->created_by = CommonFunction::getUserId();                $post->updated_by = CommonFunction::getUserId();            });            static::updating(function ($post) {                $post->updated_by = CommonFunction::getUserId();            });        }    }    /*     * ******************End of Model Class***************** */}