<?phpnamespace App\Modules\Exam\Models;use App\Libraries\CommonFunction;use Illuminate\Database\Eloquent\Model;use Illuminate\Support\Facades\Auth;class ScheduleUsers extends Model {    protected $table = 'ex_schedule_users';    protected $fillable = [        'id',        'schedule_id',        'user_id',        'no_of_right_answer',        'no_of_wrong_answer',        'mark_obtain',        'remarks',        'status',        'is_archive',        'created_by',        'updated_by',    ];        public static function boot()    {        if (Auth::user()) {            parent::boot();                        static::creating(function ($post) {                $post->created_by = CommonFunction::getUserId();                $post->updated_by = CommonFunction::getUserId();            });            static::updating(function ($post) {                $post->updated_by = CommonFunction::getUserId();            });        }    }    /*     * ******************End of Model Class***************** */}