<?phpnamespace App\Modules\Exam\Models;use App\Libraries\CommonFunction;use Illuminate\Database\Eloquent\Model;use Illuminate\Support\Facades\Auth;class UserAnswerOption extends Model {    protected $table = 'ex_user_answer_option';    protected $fillable = [        'id',        'user_answer_question_id',        'selected_schedule_question_option_id',        'is_archive',        'created_by',        'updated_by'    ];    public static function boot() {        if (Auth::user()) {            parent::boot();            static::creating(function ($post) {                $post->created_by = CommonFunction::getUserId();                $post->updated_by = CommonFunction::getUserId();            });            static::updating(function ($post) {                $post->updated_by = CommonFunction::getUserId();            });        }    }    //**********************End of Model Class***************** */}