                window.setTimeout(function() { document.myForm.submit(); }, durationMiliSecond);            })        });        function sessionTime(){            $.ajax({                url: "{{url('/exam/exam-list/exam-start')}}",                type: 'get',                success: function (response) {                }            });        }    </script>@endsection <!--- footer script--->