CREATE TRIGGER `trg_process_list_hist_insert` AFTER INSERT ON `process_list` FOR EACH ROW
BEGIN


	ref_id,
     
	process_type,
	initiated_by,
	closed_by,
	desk_id,
	status_id,
	process_desc,
	created_at,
	updated_at,
	updated_by,
	on_behalf_of_user,
	action_type,
    action_datetime,
    hash_value,
    previous_hash)
	VALUES(NEW.id, 
	NEW.ref_id,
    
	NEW.process_type_id,
	NEW.created_by,
	NEW.closed_by,
	NEW.desk_id,
	NEW.status_id,
	NEW.process_desc,
	NEW.created_at,
	NEW.updated_at,
	NEW.updated_by,
	NEW.on_behalf_of_user,
	'ONINSERT',
	NOW(),
 NEW.hash_value,
NEW.previous_hash);
    END;;

CREATE TRIGGER `trg_process_list_hist_update` AFTER UPDATE ON `process_list` FOR EACH ROW
BEGIN
	

IF NEW.status_id <> OLD.status_id or NEW.ref_id <> OLD.ref_id or NEW.company_id <> OLD.company_id 
or NEW.tracking_no <> OLD.tracking_no or NEW.json_object <> OLD.json_object or NEW.desk_id <> OLD.desk_id
or NEW.process_type_id <> OLD.process_type_id or NEW.process_desc <> OLD.process_desc 
or NEW.closed_by <> OLD.closed_by 
or NEW.hash_value <> OLD.hash_value
or NEW.previous_hash<> OLD.previous_hash
THEN   
  

        process_desc, closed_by, `created_at`, locked_by, locked_at, created_by, `updated_at`, `updated_by`,hash_value,previous_hash)

	   VALUES (NEW.id, NEW.ref_id, NEW.tracking_no, NEW.json_object, NEW.desk_id, NEW.process_type_id,
    NEW.status_id, NEW.on_behalf_of_user, NEW.process_desc, NEW.closed_by, 
NEW.`created_at`, NEW.locked_by, NEW.locked_at, NEW.created_by, NEW.`updated_at`, NEW.`updated_by`,NEW.hash_value,NEW.previous_hash);		
	SET @lastID = (SELECT id FROM process_list_hist ORDER BY id DESC LIMIT 1);
	IF @lastID IS NULL OR @lastID = '' THEN
	    SET @lastID = 0;
	END IF;    
    END IF;
	 
  UPDATE process_documents SET process_hist_id = @lastID
	  WHERE process_type_id = NEW.process_type_id AND ref_id = NEW.id AND process_hist_id = 0;
    
    END;;

